import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    fetchTodos();

    // Set up real-time subscription
    const subscription = supabase
      .channel('todos')
      .on('postgres_changes', { event: '*', schema: 'public', table: 'todos' }, (payload) => {
        fetchTodos(); // Refresh the list on any change
      })
      .subscribe();

    return () => {
      supabase.removeChannel(subscription);
    };
  }, []);

  const fetchTodos = async () => {
    const { data, error } = await supabase
      .from('todos')
      .select('*')
      .order('created_at', { ascending: false });

    if (error) {
      console.error('Error fetching todos:', error);
    } else {
      setTodos(data);
    }
  };

  const addTodo = async () => {
    if (newTask.trim() === '') return;

    const { error } = await supabase
      .from('todos')
      .insert([{ task: newTask, done: false }]);

    if (error) {
      console.error('Error adding todo:', error);
    } else {
      setNewTask('');
      // fetchTodos(); // Not needed due to real-time subscription
    }
  };

  const toggleDone = async (id, done) => {
    const { error } = await supabase
      .from('todos')
      .update({ done: !done })
      .eq('id', id);

    if (error) {
      console.error('Error updating todo:', error);
    }
    // fetchTodos(); // Not needed due to real-time subscription
  };

  const deleteTodo = async (id) => {
    const { error } = await supabase
      .from('todos')
      .delete()
      .eq('id', id);

    if (error) {
      console.error('Error deleting todo:', error);
    }
    // fetchTodos(); // Not needed due to real-time subscription
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTodo();
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>To-Do App</h1>

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Enter a new task"
          style={{
            width: '70%',
            padding: '10px',
            fontSize: '16px',
            border: '1px solid #ccc',
            borderRadius: '4px',
            marginRight: '10px'
          }}
        />
        <button
          onClick={addTodo}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Add Task
        </button>
      </div>

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {todos.map((todo) => (
          <li
            key={todo.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              marginBottom: '10px',
              backgroundColor: todo.done ? '#f0f0f0' : '#fff'
            }}
          >
            <input
              type="checkbox"
              checked={todo.done}
              onChange={() => toggleDone(todo.id, todo.done)}
              style={{ marginRight: '10px' }}
            />
            <span
              style={{
                flexGrow: 1,
                textDecoration: todo.done ? 'line-through' : 'none',
                color: todo.done ? '#888' : '#333'
              }}
            >
              {todo.task}
            </span>
            <button
              onClick={() => deleteTodo(todo.id)}
              style={{
                padding: '5px 10px',
                fontSize: '14px',
                backgroundColor: '#f44336',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                marginLeft: '10px'
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
